import React from 'react'

const API = 'https://stg-garcon.herokuapp.com/api/1/accounts/homescore/1/energy/usage/daily'

export default class DailyUsage extends React.PureComponent {
  constructor(props) {
    super(props)
    this.state = {
      usage: []
    }
  }

  componentDidMount() {
    fetch(API)
      .then((res) => res.json() )
      .then((data) => {
        console.log(data)
        this.setState({
          usage: data.daily_energy_usage
        })
      })
      .catch((error) => console.log('Oops! . There Is A Problem') )
  }

  render() {
    const items = this.state.usage.map((i) => <li key={i}>{i}</li>)

    return (
      <ul>
        {items}
      </ul>
    )
  }
}
